mapper.insert:
INSERT INTO comment_review
(commentId, treeId, userId, regDate)
VALUES(#{commentId}, #{treeId}, #{userId}, now());

mapper.delete:
delete from comment_review
where treeId = #{treeId}
 and userId = #{userId}

mapper.select:
select commentId
from comment_review cr
where treeId = #{treeId} and userId = #{userId}
